// Error reporting function (replace with your actual error service)
function reportError(error, errorInfo) {
  // Log to console in development
  if (process.env.NODE_ENV === 'development') {
    console.error('Application Error:', error, errorInfo);
  }
  
  // In production, send to error reporting service
  if (process.env.NODE_ENV === 'production') {
    // Example: Sentry, LogRocket, or custom error service
    console.log('Error would be sent to monitoring service:', { error, errorInfo });
  }
}

// Web Vitals reporting function
function reportWebVitals(metric) {
  // Log metrics in development
  if (process.env.NODE_ENV === 'development') {
    console.log('Web Vital:', metric);
  }
  
  // In production, send to analytics service
  if (process.env.NODE_ENV === 'production') {
    // Example: Google Analytics, custom analytics
    console.log('Metric would be sent to analytics:', metric);
  }
}

// Initialize performance monitoring
if (typeof window !== 'undefined') {
  // Report web vitals
  getCLS(reportWebVitals);
  getFID(reportWebVitals);
  getFCP(reportWebVitals);
  getLCP(reportWebVitals);
  getTTFB(reportWebVitals);
}

// Global error handler for unhandled promise rejections
window.addEventListener('unhandledrejection', (event) => {
  reportError(event.reason, {
    type: 'unhandledPromiseRejection',
    promise: event.promise,
    url: window.location.href,
    userAgent: navigator.userAgent,
    timestamp: new Date().toISOString()
  });
  
  // Prevent the default browser handling
  event.preventDefault();
});

// Global error handler for JavaScript errors
window.addEventListener('error', (event) => {
  reportError(event.error, {
    type: 'javascriptError',
    message: event.message,
    filename: event.filename,
    lineno: event.lineno,
    colno: event.colno,
    url: window.location.href,
    userAgent: navigator.userAgent,
    timestamp: new Date().toISOString()
  });
});

// Initialize app configuration
const initializeApp = () => {
  // Set up viewport meta tag for mobile responsiveness
  const viewport = document.querySelector('meta[name="viewport"]');
  if (!viewport) {
    const meta = document.createElement('meta');
    meta.name = 'viewport';
    meta.content = 'width=device-width, initial-scale=1.0, maximum-scale=5.0, user-scalable=yes';
    document.head.appendChild(meta);
  }

  // Set up theme-color meta tag for PWA
  const themeColor = document.querySelector('meta[name="theme-color"]');
  if (!themeColor) {
    const meta = document.createElement('meta');
    meta.name = 'theme-color';
    meta.content = '#008000'; // Kenya green
    document.head.appendChild(meta);
  }

  // Set up manifest link for PWA
  const manifest = document.querySelector('link[rel="manifest"]');
  if (!manifest) {
    const link = document.createElement('link');
    link.rel = 'manifest';
    link.href = '/manifest.json';
    document.head.appendChild(link);
  }

  // Set up favicon
  const favicon = document.querySelector('link[rel="icon"]');
  if (!favicon) {
    const link = document.createElement('link');
    link.rel = 'icon';
    link.type = 'image/png';
    link.href = '/favicon.png';
    document.head.appendChild(link);
  }

  // Set up Apple touch icon for iOS
  const appleTouchIcon = document.querySelector('link[rel="apple-touch-icon"]');
  if (!appleTouchIcon) {
    const link = document.createElement('link');
    link.rel = 'apple-touch-icon';
    link.href = '/apple-touch-icon.png';
    document.head.appendChild(link);
  }

  // Initialize Chart.js defaults (matching your original HTML setup)
  if (window.Chart) {
    window.Chart.defaults.font.family = "'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', sans-serif";
    window.Chart.defaults.color = '#374151';
    window.Chart.defaults.borderColor = '#E5E7EB';
    window.Chart.defaults.backgroundColor = 'rgba(0, 128, 0, 0.1)'; // Kenya green with transparency
  }

  // Initialize Leaflet defaults for maps (matching your original setup)
  if (window.L) {
    // Set default tile layer attribution
    window.L.tileLayer.prototype.options.attribution = '¬© <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors | MaoniBot';
  }

  // Set up default page title and description
  document.title = 'MaoniBot: Kenyan Public Sentiment & Trends';
  
  const metaDescription = document.querySelector('meta[name="description"]');
  if (!metaDescription) {
    const meta = document.createElement('meta');
    meta.name = 'description';
    meta.content = 'Real-time insights from Kenyan online conversations. Analyze public sentiment, track trending topics, and monitor hashtags across social media platforms.';
    document.head.appendChild(meta);
  }

  // Set up Open Graph meta tags for social sharing
  const ogTitle = document.querySelector('meta[property="og:title"]');
  if (!ogTitle) {
    const meta = document.createElement('meta');
    meta.setAttribute('property', 'og:title');
    meta.content = 'MaoniBot - Kenyan Public Sentiment Analysis';
    document.head.appendChild(meta);
  }

  const ogDescription = document.querySelector('meta[property="og:description"]');
  if (!ogDescription) {
    const meta = document.createElement('meta');
    meta.setAttribute('property', 'og:description');
    meta.content = 'Discover what Kenyans are talking about online. Real-time sentiment analysis and trend tracking from social media conversations.';
    document.head.appendChild(meta);
  }

  const ogImage = document.querySelector('meta[property="og:image"]');
  if (!ogImage) {
    const meta = document.createElement('meta');
    meta.setAttribute('property', 'og:image');
    meta.content = '/og-image.png';
    document.head.appendChild(meta);
  }

  // Set up Twitter Card meta tags
  const twitterCard = document.querySelector('meta[name="twitter:card"]');
  if (!twitterCard) {
    const meta = document.createElement('meta');
    meta.name = 'twitter:card';
    meta.content = 'summary_large_image';
    document.head.appendChild(meta);
  }

  // Add structured data for SEO
  const structuredData = {
    "@context": "https://schema.org",
    "@type": "WebApplication",
    "name": "MaoniBot",
    "description": "Kenyan Public Sentiment & Trends Analysis Platform",
    "url": window.location.origin,
    "applicationCategory": "Analytics",
