import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { QueryClient, QueryClientProvider } from '@tanstack/react-query';
import { ReactQueryDevtools } from '@tanstack/react-query-devtools';

// Layout Components
import { Layout } from './components/Layout';
import ErrorBoundary from './components/UI/ErrorBoundary';
import { ToastProvider, useToast } from './components/UI/Toast';
import LoadingSpinner from './components/UI/LoadingSpinner';

// Dashboard Components
import Dashboard from './components/Dashboard/Dashboard';

// Utility Components and Hooks
import { LANGUAGES, APP_CONFIG } from './utils/constants';
import { ApiProvider } from './hooks/useApi';

// CSS Imports
import './App.css';
import './styles/tailwind.css';

// Create a client for React Query
const queryClient = new QueryClient({
  defaultOptions: {
    queries: {
      retry: 3,
      retryDelay: attemptIndex => Math.min(1000 * 2 ** attemptIndex, 30000),
      staleTime: 5 * 60 * 1000, // 5 minutes
      cacheTime: 10 * 60 * 1000, // 10 minutes
      refetchOnWindowFocus: false,
      refetchOnReconnect: true,
    },
    mutations: {
      retry: 1,
    },
  },
});

// Main App Component
function App() {
  const [isInitialized, setIsInitialized] = useState(false);
  const [appError, setAppError] = useState(null);
  const [theme, setTheme] = useState('light');
  const [language, setLanguage] = useState(LANGUAGES.EN);

  // Initialize app
  useEffect(() => {
    const initializeApp = async () => {
      try {
        // Load saved preferences
        const savedTheme = localStorage.getItem('maonibot_theme') || 'light';
        const savedLanguage = localStorage.getItem('maonibot_language') || LANGUAGES.EN;
        
        setTheme(savedTheme);
        setLanguage(savedLanguage);

        // Apply theme class to document
        document.documentElement.classList.toggle('dark', savedTheme === 'dark');

        // Set page title and meta description
        document.title = APP_CONFIG.TITLE;
        const metaDescription = document.querySelector('meta[name="description"]');
        if (metaDescription) {
          metaDescription.setAttribute('content', APP_CONFIG.DESCRIPTION);
        }

        // Initialize Chart.js defaults (if using Chart.js)
        if (window.Chart) {
          window.Chart.defaults.font.family = "'Inter', sans-serif";
          window.Chart.defaults.color = '#374151';
        }

        // Check if we're online
        if (!navigator.onLine) {
          console.warn('App initialized in offline mode');
        }

        // Add global error handler
        window.addEventListener('unhandledrejection', handleUnhandledRejection);
        window.addEventListener('error', handleGlobalError);

        setIsInitialized(true);
      } catch (error) {
        console.error('Failed to initialize app:', error);
        setAppError(error);
      }
    };

    initializeApp();

    // Cleanup
    return () => {
      window.removeEventListener('unhandledrejection', handleUnhandledRejection);
      window.removeEventListener('error', handleGlobalError);
    };
  }, []);

  // Global error handlers
  const handleUnhandledRejection = (event) => {
    console.error('Unhandled promise rejection:', event.reason);
    // Optionally show a toast notification
  };

  const handleGlobalError = (event) => {
    console.error('Global error:', event.error);
    // Optionally show a toast notification
  };

  // Theme toggle function
  const toggleTheme = () => {
    const newTheme = theme === 'light' ? 'dark' : 'light';
    setTheme(newTheme);
    localStorage.setItem('maonibot_theme', newTheme);
    document.documentElement.classList.toggle('dark', newTheme === 'dark');
  };

  // Language change function
  const handleLanguageChange = (newLanguage) => {
    setLanguage(newLanguage);
    localStorage.setItem('maonibot_language', newLanguage);
  };

  // Show initialization error
  if (appError) {
    return (
      <ErrorBoundary>
        <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
          <div className="max-w-md w-full bg-white rounded-lg shadow-lg p-6 text-center">
            <div className="mx-auto w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mb-4">
              <svg className="w-8 h-8 text-red-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
              </svg>
            </div>
            <h2 className="text-xl font-bold text-gray-900 mb-2">
              Failed to Initialize MaoniBot
            </h2>
            <p className="text-gray-600 mb-4">
              Something went wrong while starting the application.
            </p>
            <button
              onClick={() => window.location.reload()}
              className="bg-kenya-green text-white px-4 py-2 rounded hover:bg-green-700 transition-colors"
            >
              Reload Application
            </button>
          </div>
        </div>
      </ErrorBoundary>
    );
  }

  // Show loading screen during initialization
  if (!isInitialized) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-center">
          <div className="mb-4">
            <img 
              src="https://via.placeholder.com/80x80?text=M&color=FFFFFF&background=008000" 
              alt="MaoniBot Logo" 
              className="w-20 h-20 mx-auto rounded-full shadow-lg"
            />
          </div>
          <LoadingSpinner size="large" color="kenya-green" />
          <h2 className="mt-4 text-xl font-semibold text-gray-800">
            Loading MaoniBot...
          </h2>
          <p className="text-gray-600 mt-2">
            Initializing Kenyan sentiment analysis platform
          </p>
        </div>
      </div>
    );
  }

  return (
    <ErrorBoundary>
      <QueryClientProvider client={queryClient}>
        <ApiProvider>
          <ToastProvider>
            <Router>
              <AppContent 
                theme={theme}
                language={language}
                onThemeToggle={toggleTheme}
                onLanguageChange={handleLanguageChange}
              />
            </Router>
            
            {/* React Query Devtools (only in development) */}
            {process.env.NODE_ENV === 'development' && (
              <ReactQueryDevtools initialIsOpen={false} />
            )}
          </ToastProvider>
        </ApiProvider>
      </QueryClientProvider>
    </ErrorBoundary>
  );
}

// App Content Component (separated for cleaner routing)
const AppContent = ({ theme, language, onThemeToggle, onLanguageChange }) => {
  const toast = useToast();
  const [isOnline, setIsOnline] = useState(navigator.onLine);

  // Handle online/offline status
  useEffect(() => {
    const handleOnline = () => {
      setIsOnline(true);
      toast.success(
        language === 'sw' ? 'Umeunganishwa tena!' : 'You are back online!',
        { duration: 3000 }
      );
    };

    const handleOffline = () => {
      setIsOnline(false);
      toast.warning(
        language === 'sw' ? 'Huna muunganisho wa intaneti' : 'You are offline',
        { duration: 0 } // Don't auto-dismiss
      );
    };

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);

    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, [toast, language]);

  // Handle route changes and analytics
  useEffect(() => {
    // Log page views (replace with actual analytics)
    const logPageView = (path) => {
      if (process.env.NODE_ENV === 'production') {
        // Analytics tracking would go here
        console.log('Page view:', path);
      }
    };

    logPageView(window.location.pathname);
  }, []);

  return (
    <div className={`app ${theme}`}>
      <Layout
        theme={theme}
        language={language}
        onThemeToggle={onThemeToggle}
        onLanguageChange={onLanguageChange}
        isOnline={isOnline}
      >
        <Routes>
          {/* Main Dashboard Route */}
          <Route 
            path="/" 
            element={
              <Dashboard 
                language={language}
                theme={theme}
                isOnline={isOnline}
              />
            } 
          />
          
          {/* Dashboard alias route */}
          <Route 
            path="/dashboard" 
            element={
              <Dashboard 
                language={language}
                theme={theme}
                isOnline={isOnline}
              />
            } 
          />

          {/* Sentiment Analysis standalone route */}
          <Route 
            path="/sentiment" 
            element={
              <SentimentPage 
                language={language}
                theme={theme}
              />
            } 
          />

          {/* Hashtag Tracker standalone route */}
          <Route 
            path="/hashtags" 
            element={
              <HashtagPage 
                language={language}
                theme={theme}
              />
            } 
          />

          {/* About page */}
          <Route 
            path="/about" 
            element={
              <AboutPage 
                language={language}
                theme={theme}
              />
            } 
          />

          {/* API status page */}
          <Route 
            path="/status" 
            element={
              <StatusPage 
                language={language}
                isOnline={isOnline}
              />
            } 
          />

          {/* 404 Not Found */}
          <Route 
            path="/404" 
            element={
              <NotFoundPage 
                language={language}
              />
            } 
          />

          {/* Redirect any unmatched routes to 404 */}
          <Route path="*" element={<Navigate to="/404" replace />} />
        </Routes>
      </Layout>

      {/* Offline indicator */}
      {!isOnline && (
        <div className="fixed bottom-0 left-0 right-0 bg-red-600 text-white p-2 text-center text-sm z-50">
          <span className="inline-block w-2 h-2 bg-red-300 rounded-full mr-2 animate-pulse"></span>
          {language === 'sw' ? 
            'Huna muunganisho wa intaneti - Baadhi ya vipengele vinaweza kutofanya kazi' : 
            'You are offline - Some features may not work'
          }
        </div>
      )}
    </div>
  );
};

// Lazy-loaded page components
const SentimentPage = React.lazy(() => import('./pages/SentimentPage'));
const HashtagPage = React.lazy(() => import('./pages/HashtagPage'));
const AboutPage = React.lazy(() => import('./pages/AboutPage'));
const StatusPage = React.lazy(() => import('./pages/StatusPage'));
const NotFoundPage = React.lazy(() => import('./pages/NotFoundPage'));

// Wrapper for lazy components
const LazyWrapper = ({ children }) => (
  <React.Suspense 
    fallback={
      <div className="flex items-center justify-center min-h-64">
        <LoadingSpinner size="large" text="Loading page..." />
      </div>
    }
  >
    {children}
  </React.Suspense>
);

// Wrap lazy components
const SentimentPageWrapped = (props) => (
  <LazyWrapper>
    <SentimentPage {...props} />
  </LazyWrapper>
);

const HashtagPageWrapped = (props) => (
  <LazyWrapper>
    <HashtagPage {...props} />
  </LazyWrapper>
);

const AboutPageWrapped = (props) => (
  <LazyWrapper>
    <AboutPage {...props} />
  </LazyWrapper>
);

const StatusPageWrapped = (props) => (
  <LazyWrapper>
    <StatusPage {...props} />
  </LazyWrapper>
);

const NotFoundPageWrapped = (props) => (
  <LazyWrapper>
    <NotFoundPage {...props} />
  </LazyWrapper>
);

// Performance monitoring
if (process.env.NODE_ENV === 'production') {
  // Web Vitals monitoring
  const reportWebVitals = (metric) => {
    console.log('Web Vital:', metric);
    // Send to analytics service
  };

  // Lazy load web-vitals
  import('web-vitals').then(({ getCLS, getFID, getFCP, getLCP, getTTFB }) => {
    getCLS(reportWebVitals);
    getFID(reportWebVitals);
    getFCP(reportWebVitals);
    getLCP(reportWebVitals);
    getTTFB(reportWebVitals);
  });
}

// Service Worker registration
if ('serviceWorker' in navigator && process.env.NODE_ENV === 'production') {
  window.addEventListener('load', () => {
    navigator.serviceWorker.register('/sw.js')
      .then((registration) => {
        console.log('SW registered: ', registration);
      })
      .catch((registrationError) => {
        console.log('SW registration failed: ', registrationError);
      });
  });
}

// Export the main App component
export default App;

// Additional app metadata for PWA
export const appMetadata = {
  name: 'MaoniBot',
  shortName: 'MaoniBot',
  description: 'Kenyan Public Sentiment & Trends Analysis Platform',
  version: '1.0.0',
  author: 'MaoniBot Team',
  themeColor: '#008000',
  backgroundColor: '#FFFFFF',
  display: 'standalone',
  orientation: 'portrait-primary',
  startUrl: '/',
  scope: '/'
};

// App configuration for development
export const devConfig = {
  showDevtools: process.env.NODE_ENV === 'development',
  enableHotReload: process.env.NODE_ENV === 'development',
  apiBaseUrl: process.env.REACT_APP_API_BASE_URL || 'http://localhost:8000/api',
  websocketUrl: process.env.REACT_APP_WS_URL || 'ws://localhost:8000/ws',
  enableAnalytics: process.env.NODE_ENV === 'production',
};

// Export utilities for testing
export { queryClient };
